  return (
    <>
      <Header />
      <h1>Hello World!</h1>
      <p>
        Name: {name_first}, {name_last}
      </p>
      <hr />
      <div>
        <label>First Name</label> <input onChange={handleChange} type="text" />
      </div>
    </>
  );
};

export default Home;
